import { useState } from "react";

const Blog = ({ blog }) => {

  const [visible, setVisible] = useState(false)
  const hideWhenVisible = { display: visible ? 'none' : '' }
  const showWhenVisible = { display: visible ? '' : 'none' }
  const toggleVisibility = () => {
    setVisible(!visible)
  }
	const blogStyle = {
		paddingTop: 10,
		paddingLeft: 2,
		border: "solid",
		borderWidth: 1,
		marginBottom: 5,
  };
  const showStyle = {
    display : visible
  }
	return (
		<div style={hideWhenVisible}>
        <p>title :{blog.title} <button onClick={toggleVisibility}>click it</button></p>
        <div style = {showStyle}>
				<p>author :{blog.author}</p>
				<p>url :{blog.url}</p>
				<p>likes :{blog.likes}</p>
        </div>
		</div>
	);
};

export default Blog;
