import { useState } from "react";
import axios from "axios";

const Blog = ({ blog }) => {
	const [visible, setVisible] = useState(true);
	const [likes, setLikes] = useState(blog.likes ? null : 0)
	const hideWhenVisible = { display: visible ? "none" : "" };
	const toggleVisibility = () => {
		setVisible(!visible);
	};
	const blogStyle = {
		paddingTop: 10,
		paddingLeft: 2,
		border: "solid",
		borderWidth: 1,
		marginBottom: 5,
		margin: 0,
	};
	const updatebloglikes = async () => {
		var axios = require("axios");
		var data = JSON.stringify({
			title: blog.title,
			author: blog.author,
			url: blog.url,
			likes: blog.likes + 1,
		});

		var config = {
			method: "post",
			url: "http://localhost:3003/api/blogs/update",
			headers: {
				"Content-Type": "application/json",
			},
			data: data,
		};

		axios(config)
			.then(function (response) {
				console.log(JSON.stringify(response.data));
				this.forceupdate()
			})
			.catch(function (error) {
				console.log(error);
			});
	};
	return (
		<div style={blogStyle}>
			<b>
				<p>
					title :{blog.title} <button onClick={toggleVisibility}>show</button>
				</p>
				<div style={hideWhenVisible}>
					<p>author :{blog.author}</p>
					<p>url :{blog.url}</p>
					<p>
						likes :{blog.likes}
						<button onClick={() => updatebloglikes()}>Like</button>
					</p>
				</div>
			</b>
		</div>
	);
};

export default Blog;
