import { useState } from "react";

const Blog = ({ blog }) => {

  const [visible, setVisible] = useState(true)
  const hideWhenVisible = { display: visible ? 'none' : '' }
  const toggleVisibility = () => {
    setVisible(!visible)
  }
	const blogStyle = {
		paddingTop: 10,
		paddingLeft: 2,
		border: "solid",
		borderWidth: 1,
		marginBottom: 5,
  };
	return (
		<div style={blogStyle}>
      <b>
        <p>title :{blog.title} <button onClick={toggleVisibility}>show</button></p>
        <div style = {hideWhenVisible}>
				<p>author :{blog.author}</p>
				<p>url :{blog.url}</p>
				<p>likes :{blog.likes}<button onClick={() => {console.log("Like + 1")}}>Like</button></p>
        </div>
      </b>
		</div>
	);
};

export default Blog;
