import axios from 'axios'
const baseUrl = '/api/blogs'

let token = null

const setToken = newToken => {
  token = `bearer ${newToken}`
}

const getAll = () => {
  const request = axios.get(baseUrl)
  return request.then(response => response.data)
}

const create = async newObject => {
  const config = {
    headers: { Authorization: token },
  }

  const response = await axios.post(baseUrl, newObject, config)
  return response.data
}

const update = (id, newObject) => {
  const request = axios.put(`${ baseUrl }/${id}`, newObject)
  return request.then(response => response.data)
}

const deleteblog = (id) => {
  const request = axios.delete(`${baseUrl}/${id}`)
  return request.then(response => response.data)

  var axios = require('axios');

var config = {
  method: 'delete',
  url: 'http://localhost:3003/api/blogs/6360953a1b585f8d166b383c',
  headers: { 
    'Authorization': 'Bearer eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9.eyJ1c2VybmFtZSI6Ik1veWFuIiwiaWQiOiI2MzYwOTIxZTY0NjBlNzQwYzA0YzVjZDciLCJpYXQiOjE2NjcyNzM3MTN9.t-FsfrrDi-pLjRQKn-Jyr-jyh7axjYJ15yMQkhFIns8'
  }
};

axios(config)
.then(function (response) {
  console.log(JSON.stringify(response.data));
})
.catch(function (error) {
  console.log(error);
});

}

export default { getAll, create, update, setToken, deleteblog}
